pragma solidity ^0.8.0;
import "@openzeppelin/contracts/access/Ownable.sol";

contract B {}

contract PricingChain is Ownable {
    enum State {
        OPEN,
        CLOSE
    }

    struct Product {
        uint id;
        string ipfsID;
        uint price;
        uint count_participant_pricing;
        mapping(address => uint) participant_pricing;
        State state;
    }

    // đếm xem có bao nhiêu sản phẩm
    uint private _countProducts = 0;
    // danh sách sản phẩm, uint là id của sản phẩm
    Product[] private _products;

    // danh sách

    constructor() {
        // add null _products[0]
        _products.push();
    }

    function addProduct(string memory ipfsID)
        public
        onlyOwner
        returns (uint id)
    {
        _countProducts++;
        Product storage p = _products.push();
        p.id = _countProducts;
        p.ipfsID = ipfsID;
        p.count_participant_pricing = 0;
        p.state = State.CLOSE;
        return _countProducts;
    }

    function get() public pure returns (uint8[4] memory) {
        uint8[4] memory arr = [1, 2, 3, 6];
        return arr;
    }

    /*  function getProduct(uint ID)
        public
        view
        returns (
            uint id,
            string memory ipfsID,
            uint price,
            uint count_participant_pricing,
            address[] memory participants,
            uint[] memory prices
        )
    {
        Product storage p = _products[ID];
        address[] memory participants;
        uint[] memory prices;

        for (  uint i = 1 ; i <= p.count_participant_pricing; i++) {
            participants.push(p.participant_pricing[i]);
            prices.push(p.participant_pricing[i]);
        }


    }*/

    function pricing(uint productID, uint price) public {
        // id nằm trong khoản đếm 1 đến _countProducts
        require(
            productID > 0 && productID <= _countProducts,
            "productID is not exist"
        );
        // vượt quá số lần định giá
        require(
            _products[productID].count_participant_pricing <= 10,
            "exceed the amount of the pricing time"
        );

        _products[productID].participant_pricing[msg.sender] = price;
        _products[productID].count_participant_pricing++;
    }
}
